mapper.select:
		
		SELECT RE.* FROM (
       		SELECT R.*,ROWNUM RN FROM 
       		REPAIR R
       		WHERE 1 = 1
       		
       		
       		
       			AND budno like #{buildingId}
       		
     		
      			AND rtype = #{typeId}
     		       		
       		
       			AND HANDLESTATUS IN 
       				
   	   					#{checkedStatusId}
    				  
       		
       		
       			
       				AND REPORTTIME like #{likeStartTime}
       			
       			
       				AND REPORTTIME >= #{startTime} AND REPORTTIME <= #{endTime}
       			
       		

       		 ORDER BY RID ASC) RE
		WHERE 
			RN>(#{currentPage}-1)*#{pageSize} 
		AND RN<=#{currentPage}*#{pageSize}

		
       		SELECT count(*) FROM 
       		REPAIR R
       		WHERE 1 = 1
       		
       		
       		
       			
       			AND budno like #{buildingId}
       		
     		
      			AND rtype = #{typeId}
     		       		
       		
      			AND HANDLESTATUS IN 
       				
   	   					#{checkedStatusId}
				  
    			
    		 
    		
       			
       				AND REPORTTIME like #{likeStartTime}
       			
       			
       				AND REPORTTIME >= #{startTime} AND REPORTTIME <= #{endTime}
       			
			
       		 ORDER BY RID ASC

		
		SELECT DORM.* FROM (
       		SELECT D.*,ROWNUM RN FROM DORMITORY D WHERE BUILDING_ID = #{bid} ORDER BY DNO ASC) DORM
		WHERE 
			RN>(#{currentPage}-1)*#{pageSize} 
		AND RN<=#{currentPage}*#{pageSize}
