mapper.select:
		
		SELECT RE.* FROM (
       		SELECT R.*,ROWNUM RN FROM 
       		REPAIR R
       		WHERE
       		
       			HANDLESTATUS IN 
       				
   	   					#{handleStatus}
    				  
    			AND
       		
       		
       			 1 = 1 AND REPORTTIME >= #{startTime} AND REPORTTIME <= #{endTime} AND
       		
       		
       			 1 = 1 AND substr(budno,1,2) = #{buildingId}
       		
       		 ORDER BY RID ASC) RE
		WHERE 
			RN>(#{currentPage}-1)*#{pageSize} 
		AND RN<=#{currentPage}*#{pageSize}

		
		SELECT DORM.* FROM (
       		SELECT D.*,ROWNUM RN FROM DORMITORY D WHERE BUILDING_ID = #{bid} ORDER BY DNO ASC) DORM
		WHERE 
			RN>(#{currentPage}-1)*#{pageSize} 
		AND RN<=#{currentPage}*#{pageSize}
